shrimp.code:
#ifdef RAYOPTIONS #undef RAYOPTIONS #endif
#if RENDERER==_3delight #define RAYOPTIONS "label", $(label), "subset", $(subset), "bias", \ $(bias), "hitmode", $(hitmode), "samplecone", \ $(samplecone), "samples", $(samples) #elif RENDERER==pixie #define RAYOPTIONS "samples", $(samples), "bias", $(bias), "samplecone", \ $(samplecone), "sampleBase", $(samplebase), \ "maxdist", $(maxdist), "label", $(label) #elif RENDERER==prman #define RAYOPTIONS "samples", $(samples), "minsamples", $(minsamples), \ "samplecone", $(samplecone), "samplebase", \ $(samplebase), "bias", $(bias), "label", $(label), \ "subset", $(subset), "hitmode", $(hitmode) #else #define NOSHADEOP #endif
/* Seems only 3delight, prman, and pixie have transmission() shadeop */ #ifdef NOSHADEOP $(Ctr) = color(1); #else $(Ctr) = transmission( $(pointfrom), $(pointto), RAYOPTIONS ); #endif
