shrimp.rsl_code:

#define $(blockname)_$(rsamples)

aov_surfacecolor += $(Cdiff);
aov_specularcolor += $(Cspec);

	$(Ci) = LocIllumAshShir(	$(N), $(I), $(xdir), $(Cdiff), $(Cspec),
								$(Kd), $(Ks), $(nu), $(nv),
								DECLARE_AOV_PARAMETERS );
#ifdef $(blockname)_0
	$(Ci) = $(Ci);
#else /* add environment sampling */
	aov_reflection += $(Crefl) * EnvIllumAshShir(
							faceforward( $(N), $(I) ), -$(I),
							$(xdir), $(Kd), $(Ks), $(nu), $(nv),
							$(rsamples), $(envmap) );
	$(Ci) += aov_reflection;
#endif /* $(blockname)_$(rsamples) */
#undef $(blockname)_$(rsamples)
