shrimp.rsl_code:
aov_surfacecolor += $(Cdiff);

/* Pixie's diffuse() has 2 forms */
#define DIFFTYPE $(angle)
#if RENDERER == pixie && DIFFTYPE != 0
	aov_diffuse += $(Kd) * diffuse( $(PP), $(norm), $(angle) );
#else
	aov_diffuse += $(Kd) * diffuse( $(norm) );
#endif
#undef DIFFTYPE

$(Ci) = aov_surfacecolor * aov_diffuse;
