shrimp.code:
#define var_$(var_type)
#ifdef var_float
	printf( concat( $(str), " %f\n"), $(var) );
#elif var_color
	printf( concat( $(str), " %c\n"), $(var) );
#elif var_matrix
	printf( concat( $(str), " %8m\n"), $(var) );
#elif var_string
	printf( concat( $(str), " %s\n"), $(var) );
#else
	printf( concat( $(str), " %p\n"), $(var) );
#endif
	$(val) = $(var);
