shrimp.rsl_code:
#define var_$(var:type)
#ifdef var_float
	printf( concat( $(str), " %f\n"), $(var) );
#endif
#ifdef var_color
	printf( concat( $(str), " %c\n"), $(var) );
#endif
#ifdef var_matrix
	printf( concat( $(str), " %m\n"), $(var) );
#endif
#ifdef var_string
	printf( concat( $(str), " %s\n"), $(var) );
#else
	printf( concat( $(str), " %p\n"), $(var) );
#endif

	$(val) = $(var);
