shrimp.rsl_code:

#define $(blockname)_$(value:type)
#ifdef $(blockname)_float
	$(value) = ( $(bias) > 0) ? pow( $(input), log($(bias)) / log(0.5)) : 0;
#else
	float $(blockname)_xtmp = comp( $(value:type) $(input), 0);
	float $(blockname)_ytmp = comp( $(value:type) $(input), 1);
	float $(blockname)_ztmp = comp( $(value:type) $(input), 1);
	$(value) = $(value:type) (	bias( $(blockname)_xtmp, $(bias)),
							bias( $(blockname)_ytmp, $(bias)),
							bias( $(blockname)_ztmp, $(bias)) );
#endif /* $(blockname)_$(value:type) */
#undef $(blockname)_$(value:type)
