shrimp.code:
#define $(val)_$(val:type)
#ifdef $(val)_float
	$(val) = ($(bias) > 0) ? pow($(nput), log($(bias)) / log(0.5)) : 0;
#else
	float xtmp = xcomp( $(val:type) $(nput));
	float ytmp = ycomp( $(val:type) $(nput));
	float ztmp = zcomp( $(val:type) $(nput));
	$(val) = $(val:type) (bias(xtmp, $(bias)), bias(ytmp, $(bias)), bias(ztmp, $(bias)));
#endif
