shrimp.rsl_code:
#define $(val)_$(val:type)
#ifdef $(val)_float
	$(val) = ($(bias) > 0) ? pow($(nput), log($(bias)) / log(0.5)) : 0;
#else
	float xtmp = comp( $(val:type) $(nput), 0);
	float ytmp = comp( $(val:type) $(nput), 1);
	float ztmp = comp( $(val:type) $(nput), 1);
	$(val) = $(val:type) (bias(xtmp, $(bias)), bias(ytmp, $(bias)), bias(ztmp, $(bias)));
#endif
