shrimp.rsl_code:

#define $(blockname)_$(val:type)
#ifdef $(blockname)_float
	$(val) = ( $(bias) > 0) ? pow( $(nput), log($(bias)) / log(0.5)) : 0;
#else
	float $(blockname)_xtmp = comp( $(val:type) $(nput), 0);
	float $(blockname)_ytmp = comp( $(val:type) $(nput), 1);
	float $(blockname)_ztmp = comp( $(val:type) $(nput), 1);
	$(val) = $(val:type) (	bias( $(blockname)_xtmp, $(bias)),
							bias( $(blockname)_ytmp, $(bias)),
							bias( $(blockname)_ztmp, $(bias)) );
#endif /* $(blockname)_$(val:type) */
#undef $(blockname)_$(val:type)
