shrimp.rsl_code:

#define $(blockname)_$(value:type)
#ifdef $(blockname)_float
	$(value) = pow( $(nput), 1/max(EPS, $(gamma)));
#else
	float $(blockname)_xtmp = comp( $(value:type) $(nput), 0);
	float $(blockname)_ytmp = comp( $(value:type) $(nput), 1);
	float $(blockname)_ztmp = comp( $(value:type) $(nput), 2);
	$(value) = $(value:type) (
				pow( $(blockname)_xtmp, 1/max(EPS, $(gamma)) ),
				pow( $(blockname)_ytmp, 1/max(EPS, $(gamma)) ),
				pow( $(blockname)_ztmp, 1/max(EPS, $(gamma)) ) );
#endif /* $(blockname)_$(value:type) */
#undef $(blockname)_$(value:type)
