shrimp.code:
#define $(val)_$(val:type)
#ifdef $(val)_float
	$(val) = pow( $(nput), 1 / max(0.001, $(gammaval)));
#else
	float xtmp = comp( $(val:type) $(nput), 0);
	float ytmp = comp( $(val:type) $(nput), 1);
	float ztmp = comp( $(val:type) $(nput), 2);
	$(val) = $(val:type) ( pow( xtmp, 1/ max(0.001, $(gammaval))),
							pow( ytmp, 1/ max(0.001, $(gammaval))),
							pow( ztmp, 1/ max(0.001, $(gammaval))) );
#endif
