shrimp.rsl_code:

#define $(blockname)_$(val:type)
#ifdef $(blockname)_float
	$(val) = pow( $(nput), 1/max(EPS, $(gammaval)));
#else
	float $(blockname)_xtmp = comp( $(val:type) $(nput), 0);
	float $(blockname)_ytmp = comp( $(val:type) $(nput), 1);
	float $(blockname)_ztmp = comp( $(val:type) $(nput), 2);
	$(val) = $(val:type) (
				pow( $(blockname)_xtmp, 1/max(EPS, $(gammaval)) ),
				pow( $(blockname)_ytmp, 1/max(EPS, $(gammaval)) ),
				pow( $(blockname)_ztmp, 1/max(EPS, $(gammaval)) ) );
#endif /* $(blockname)_$(val:type) */
#undef $(blockname)_$(val:type)
