shrimp.code:
#define $(val)_$(val:type)
#ifdef $(val)_float
	$(val) = pow($(nput), 1/$(gammaval));
#else
	float xtmp = xcomp( $(val:type) $(nput));
	float ytmp = ycomp( $(val:type) $(nput));
	float ztmp = zcomp( $(val:type) $(nput));
	$(val) = $(val:type) (pow(xtmp, 1/$(gammaval)), pow(ytmp, 1/$(gammaval)), pow(ztmp, 1/$(gammaval)));
#endif
