shrimp.code:
#define $(val)_$(var_type)
#ifdef $(val)_float
	$(val) = pow($(nput), 1/$(gammaval));
#else
	float xtmp = xcomp( $(var_type) $(nput));
	float ytmp = ycomp( $(var_type) $(nput));
	float ztmp = zcomp( $(var_type) $(nput));
	$(val) = $(var_type) (pow(xtmp, 1/$(gammaval)), pow(ytmp, 1/$(gammaval)), pow(ztmp, 1/$(gammaval)));
#endif
