block.rsl_code:
        aov_specularcolor = $(Cspec);

        #if RENDERER==_3delight

        uniform string $(blockname)_raytype = "";
        rayinfo("type", $(blockname)_raytype);

        if ($(blockname)_raytype != "diffuse")
        {
            aov_specular += $(Ks) *
                cooktorrance(
                    $(N), $(I), $(direction),
                    $(ior), $(extinction), $(roughness),
                    $(distribution), $(attenuation), $(category));

            shader $(blockname)_gi_light = getshader("gi_light");

            uniform string $(blockname)_envmap = "";
            uniform string $(blockname)_envspace = "";

            if ($(blockname)_gi_light != null)
            {
                $(blockname)_envmap = $(blockname)_gi_light->envmap;
                $(blockname)_envspace = $(blockname)_gi_light->envspace;
            }

            aov_reflection += $(Ks) *
                trace(P, $(N), "bsdf", "cook-torrance",
                    "wo", -$(I), "roughness", $(roughness),
                    "samples", $(samples), "eta", $(ior),
                    "samplearealights", 1,
                    "weight", $(Cspec),
                    "environmentmap", $(blockname)_envmap,
                    "environmentspace", $(blockname)_envspace);
        }

        #else

        uniform string $(blockname)_raytype = "";
        rayinfo("type", $(blockname)_raytype);

        if ($(blockname)_raytype != "diffuse")
        {
            aov_specular += $(Ks) *
                cooktorrance(
                    $(N), $(I), $(direction),
                    $(ior), $(extinction),
                    $(roughness), $(distribution),
                    $(attenuation), $(category));
        }

        #endif

		$(Ci) = aov_specularcolor * (aov_specular + aov_reflection);

        aov_specularcolor = $(Cspec);

        #if RENDERER==_3delight

        uniform string $(blockname)_raytype = "";
